open System.Collections.Generic

let input = "" |> Seq.toList

let rec scanInput (input: char list) (lastThree: char list) index uniqueLength =
    match input with
    | head :: tail ->
        let newMarker = if lastThree.Length >= uniqueLength then lastThree[..(uniqueLength - 2)] @ [head] else lastThree @ [head]
        let newDistinct = List.distinct newMarker
        if newDistinct.Length < newMarker.Length || index < uniqueLength then
            scanInput tail (if newMarker.Length >= uniqueLength then newMarker.Tail else newMarker) (index + 1) uniqueLength
        else
            index
    | [] -> index
    
let result = scanInput input [] 1 4

printfn $"Start-of-packet is {result}"

let messageResult = scanInput input [] 1 14
printfn $"Start-of-message is {messageResult}"